/**
 * @ignore
 */
function tellerPosFromContract(rawTellerPos) {
  const tellerPos = {};
  try {
    // TODO more resilient
    // raw : 1234567 -> 1.234567
    tellerPos.lat = rawTellerPos[0] / (10 ** COORD_PRECISION);
    tellerPos.lng = rawTellerPos[1] / (10 ** COORD_PRECISION);
    tellerPos.zoneId = Number(rawTellerPos[2]);

    tellerPos.escrowBalance = Number(Ethers.utils.formatEther(rawTellerPos[3]));
  } catch (e) {
    console.error(e);
    throw new TypeError(`Invalid teller position: ${e.message}`);
  }
  return tellerPos;
}

/**
 * @ignore
 */
function tellerProfileFromContract(rawTellerProfile) {
  const tellerProfile = {};

  // TODO more resilient
  try {
    tellerProfile.rates = rawTellerProfile.rates / 100;
    tellerProfile.volumeTrade = Number(Ethers.utils.formatEther(rawTellerProfile.volumeTrade));
    tellerProfile.nbTrade = rawTellerProfile.nbTrade.toNumber();
    tellerProfile.name = toUtf8(rawTellerProfile.name);
    tellerProfile.currencyId = rawTellerProfile.currency;
    tellerProfile.avatarId = rawTellerProfile.avatar;
    tellerProfile.messengerAddr = toUtf8(rawTellerProfile.telAddr);
  } catch (e) {
    console.error(e);
    throw new TypeError(`Invalid teller profile: ${e.message}`);
  }
  return tellerProfile;
}

/**
 * @ignore
 */
function sellPointToContract(rawSellPoint) {
  const sellPoint = {};

  try {
    // 1.234567 -> 1234567
    sellPoint.lat = Math.floor(rawSellPoint.lat.toFixed(COORD_PRECISION + 1)
      * (10 ** COORD_PRECISION));
    sellPoint.lng = Math.floor(rawSellPoint.lng.toFixed(COORD_PRECISION + 1)
      * (10 ** COORD_PRECISION));
    sellPoint.zone = rawSellPoint.zone;
    sellPoint.rates = Math.floor(rawSellPoint.rates * 100);
    sellPoint.avatar = rawSellPoint.avatar;
    sellPoint.currency = rawSellPoint.currency;
    sellPoint.telegram = Ethers.utils.toUtf8Bytes(rawSellPoint.telegram);
    sellPoint.username = Ethers.utils.toUtf8Bytes(rawSellPoint.username);
  } catch (e) {
    console.error(e);
    throw new TypeError(`Invalid teller profile: ${e.message}`);
  }
  return sellPoint;
}

export default {
  tellerPosFromContract,
  tellerProfileFromContract,
  sellPointToContract,
};

import Ethers from 'ethers';

/**
 * Retrieve ethereum provider
 * Only network is needed
 *
 * @param {object}  opts
 * @param {String}  opts.network       Name of network ('homestead', 'ropsten', 'rinkeby', 'kovan')
 * @param {?String} opts.rpcURL       JSON RPC provider URL
 * @param {?String} opts.infuraKey    INFURA API Key
 * @param {?String} opts.etherscanKey Etherscan API Key
 * @return {Provider}
 * @ignore
 */
function getProvider(opts) {
  if (!opts.network) throw new TypeError('Unable to get provider, need network');
  const providers = [];

  if (opts.rpcURL) {
    providers.push(new Ethers.providers.JsonRpcProvider(opts.rpcURL, opts.network));
  }
  if (opts.etherscanKey) {
    providers.push(new Ethers.providers.EtherscanProvider(opts.network, opts.etherscanKey));
  }
  // if (opts.infuraKey) {
  //   providers.push(new Ethers.providers.InfuraProvider(opts.network, opts.infuraKey));
  // }

  providers.push(Ethers.providers.getDefaultProvider(opts.network));
  return new Ethers.providers.FallbackProvider(providers);
}

export default {
  getProvider,
};

/**
 * @ignore
 */
export const validateSellPoint = (teller) => {
  if (!teller.lat || Number.isNaN(teller.lat) || teller.lat > 90 || teller.lat < -90) {
    return { error: true, msg: 'Invalid latitude' };
  }
  if (!teller.lng || Number.isNaN(teller.lng) || teller.lng > 180 || teller.lng < -180) {
    return { error: true, msg: 'Invalid longitude' };
  }
  if (!teller.zone || !Number.isInteger(teller.zone)) {
    return { error: true, msg: 'Invalid zone' };
  }
  if (!teller.rates || teller.rates <= 0 || teller.rates > 100) {
    return { error: true, msg: 'Invalid rates' };
  }
  if (!teller.avatar || !Number.isInteger(teller.avatar) || teller.avatar < 0) {
    return { error: true, msg: 'Invalid avatar' };
  }
  if (!teller.currency || !Number.isInteger(teller.currency) || teller.currency < 0) {
    return { error: true, msg: 'Invalid currency' };
  }
  if (!teller.telegram || teller.telegram.length < 3 || teller.telegram.length > 30) {
    return { error: true, msg: 'Invalid telegram' };
  }
  if (!teller.amount || Number.isNaN(teller.amount) || teller.amount < 0.01) {
    return { error: true, msg: 'Invalid amount' };
  }
  if (!teller.username || teller.username.length < 3 || teller.username.length > 30) {
    return { error: true, msg: 'Invalid username' };
  }
  return {};
};

/**
 * @ignore
 */
export const validateSendCoin = (tsx) => {
  if (!tsx.receiver) {
    return { error: true, msg: 'Invalid receiver' };
  }
  if (!tsx.amount || Number.isNaN(tsx.amount) || tsx.amount < 0.001) {
    return { error: true, msg: 'Invalid amount' };
  }
  return {};
};

/**
 * @ignore
 */
export const validatePassword = (password) => {
  if (!password || (typeof password) !== 'string' || password.length < 1) {
    return { error: true, msg: 'Invalid password' };
  }
  return {};
};
